import React, { Component } from 'react';
import Popup from '../components/Popup';

class Form extends Component {
    state = {
        firstName: '',
        lastName: '',
        phoneNumber: '',
        popupKey: false
    }

      handleSubmit = (event) => {
        event.preventDefault();
        const firstName = event.target[0].value;
        const lastName = event.target[1].value;
        const phoneNumber = event.target[2].value;
        console.log(firstName);
        this.setState({
          firstName, lastName, phoneNumber
        });
        this.setState({
          popupKey: !this.state.popupKey
        })
      }

    render () {
      if (this.state.popup) {
        console.log(popup);
     //   return <Popup name={this.state.firstName}/>
      }
        return (
    <div class="row" text-align="center">
    <form class="col s12" onSubmit={this.handleSubmit}>
        <div class="row">
        <div class="input-field col s12">
        <label>
          First Name:
          <input type="text" value={this.state.firstName} onChange={(event) => this.setState({firstName: event.target.value})} />
        </label>
        </div>
        </div><br />
        <div class="row">
        <div class="input-field col s12">
        <label>
          Last Name:
          <input type="text" value={this.state.lastName} onChange={(event) => this.setState({lastName: event.target.value})} />
        </label>
        </div>
        </div><br />
        <div class="row">
        <div class="input-field col s12">
        <label>
          Phone Number:
          <input type="number" value={this.state.phoneNumber} onChange={(event) => this.setState({phoneNumber: event.target.value})} />
        </label>
        </div>
        </div><br /><br />
        <label>
        <input type="submit" value="Submit" />
        </label>
      </form>
      </div>
        );
    }
}



export default Form;
