import React from 'react';
import axios from '../axios-instance';

const Popup = (props) => {
    console.log(props.name);



    return (
        <div>
            <h4>Thank you for registering {props.name} {props.last} </h4>
            <button onClick={handleDatabase = (event) => {
        const visitorInfo = {
            firstName: props.name,
            lastName: props.last,
            phoneNumber: props.phone
        }
        axios.post('/visitorInfo.json', visitorInfo)
        .then(response => console.log(response))
        .catch(error => console.log(error));
    }}>Click Here If You Are Done</button>
            <h5><a href='/tour'>See more detail about what we do</a></h5>
        </div>
    );
}


export default Popup;
