import React, { Component } from 'react';

class Form extends Component {
    state = {
        firstName: '',
        lastName: '',
        startDate: '',
        endDate: '',
        placesToVisit: '',
        howMany: '',
        ageRange: '',
        email: '',
        phoneNumber: ''
    }

    //   handleSubmit(event) {
    //     alert('A name was submitted: ' + this.state.value);
    //     event.preventDefault();
    //   }

    render () {
        return (
    <form>
        <label>
          First Name:
          <input type="text" value={this.state.firstName} onChange={this.setState({firstName: event.target.value})} />
        </label>
        <label>
          Last Name:
          <input type="text" value={this.state.lastName} onChange={this.setState({lastName: event.target.value})} />
        </label>
        <label>
          Tour Start-Date :
          <input type="text" value={this.state.startDate} onChange={this.setState({startDate: event.target.value})} />
        </label>
        <label>
          Tour End-Date:
          <input type="text" value={this.state.endDate} onChange={this.setState({endDate: event.target.value})} />
        </label>
        <label>
          Places To Visit:
          <input type="text" value={this.state.placesToVisit} onChange={this.setState({placesToVisit: event.target.value})} />
        </label>
        <label>
          Number of Visitors:
          <input type="number" value={this.state.howMany} onChange={this.setState({howMany: event.target.value})} />
        </label>
        <label>
          Visitors Age Range:
          <input type="number" value={this.state.ageRange} onChange={this.setState({ageRange: event.target.value})} />
        </label>
        <label>
          Mail:
          <input type="text" value={this.state.email} onChange={this.setState({email: event.target.value})} />
        </label>
        <label>
          Phone Number:
          <input type="number" value={this.state.phoneNumber} onChange={this.setState({phoneNumber: event.target.value})} />
        </label>
        <input type="submit" value="Submit" />
      </form>
        );
    }
}



export default Form;
