import Danakil from '../assets/image/Danakil.jpg';
import bluenile from '../assets/image/bluenile.jpg';
import semen from '../assets/image/semen.jpg';
import lali from '../assets/image/lali.jpg';
import './Ethiopia.css';


class Ethiopia extends Component {
    state = {
        register: false
    }

    clickHandler = (e) => {
        this.setState({
            register: !this.state.register
        });
    }

    render () {
        if (this.state.register) {
            return (
               <Form />
            );
        }
        return (
            <div class="container">
                <h4 className="center">Ethiopia</h4>
                <p>The landscape of this wonderful country is no less varied with lakes rivers, rocky mountains, grasslands, and lowlands.
Top it off with incredible wildlife, remarkable tribal people, delicious food, and wonderful weather.
But make no mistake, this is not merely a holiday destination. Ethiopia will move you, inspire you, and be a journey that you’ll never forget.
Get registered here and Let’s explore the best places to visit in Ethiopia</p>
