import React, { Component } from 'react';

class Form extends Component {
    state = {
        firstName: '',
        lastName: '',
        phoneNumber: ''
    }

    //   handleSubmit(event) {
    //     alert('A name was submitted: ' + this.state.value);
    //     event.preventDefault();
    //   }

    render () {
        return (
    <div class="row" text-align="center">
    <form class="col s12" onSubmit={this.handleSubmit}>
        <div class="row">
        <div class="input-field col s12">
        <label>
          First Name:
          <input type="text" value={this.state.firstName} onChange={(event) => this.setState({firstName: event.target.value})} />
        </label>
        </div>
        </div><br />
        <div class="row">
        <div class="input-field col s12">
        <label>
          Last Name:
          <input type="text" value={this.state.lastName} onChange={(event) => this.setState({lastName: event.target.value})} />
        </label>
        </div>
        </div><br />
        <div class="row">
        <div class="input-field col s12">
        <label>
          Phone Number:
          <input type="number" value={this.state.phoneNumber} onChange={(event) => this.setState({phoneNumber: event.target.value})} />
        </label>
        </div>
        </div><br /><br />
        <label>
        <input type="submit" value="Submit" />
        </label>
      </form>
      </div>
        );
    }
}



export default Form;
