import React, { Component } from 'react';


class Ethiopia extends Component {
    state = {
        register: false
    }

    clickHandler = (e) => {
        this.setState({
            register: !this.state.register
        });
    }

    render () {
        if (this.state.register) {
            console.log(this.state.register);
            return (
               <h2>okay register</h2>
            );
        }
        return (
            <div className="container">
                <h4 className="center">Ethiopia</h4>
                <p>The discount code's location is returned in the location header, not in the DiscountCode object itself. Depending on your HTTP client, the location of the discount code might follow the location header automatically.</p>
                <button onClick={this.clickHandler}>Register Me</button>
            </div>
        );
    }

    }

export default Ethiopia;
